import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { isDark, toggleTheme } = useTheme();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', { email, password });
  localStorage.setItem('token', res.data.token);
  // set axios default header for subsequent requests
  axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
  navigate('/dashboard');
    } catch (err) {
  setError(err.response?.data?.message || err.message || 'Login failed');
    }
  };

  return (
    <>
      <div className="site-header">
        <div className="brand">MERN Agent System</div>
        <button 
          onClick={toggleTheme}
          className="theme-toggle"
          aria-label="Toggle theme"
        >
          {isDark ? '‚òÄÔ∏è' : 'üåô'}
        </button>
      </div>
      <div className="app-container">
    <h2>Admin Login</h2>
    <p style={{marginBottom:'16px',color:'var(--text-secondary)'}}>Use <b>admin@example.com</b> / <b>Admin@123</b> to login.<br/>After login, you can add agents, upload lists, and view assignments.</p>
        <form onSubmit={handleSubmit}>
          <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
          <button type="submit">Login</button>
        </form>
        {error && <p className="error">{error}</p>}
      </div>
    </>
  );
}

export default Login;
