import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTheme } from '../contexts/ThemeContext';

export default function Header() {
  const navigate = useNavigate();
  const { isDark, toggleTheme } = useTheme();
  const logout = () => { localStorage.removeItem('token'); navigate('/login'); };
  
  return (
    <header className="site-header">
      <div className="brand">MERN CRM</div>
      <nav className="site-nav">
        <Link to="/dashboard/agents">Agents</Link>
        <Link to="/dashboard/upload">Upload</Link>
        <Link to="/dashboard/lists">Lists</Link>
      </nav>
      <div className="site-actions">
        <button className="theme-toggle small" onClick={toggleTheme}>
          {isDark ? '‚òÄÔ∏è' : 'üåô'}
        </button>
        <button className="small" onClick={logout}>Logout</button>
      </div>
    </header>
  );
}
